mapper.select:
SELECT

FROM
cms_content content
WHERE
content.del_flag = 0
AND EXISTS (
SELECT 1 FROM cms_ih_patient_content patient WHERE content.id = patient.content_id AND patient.del_flag = 0 AND patient.line_id = 3

    and patient.module_id = #{request.moduleId}


    and patient.category_id = #{request.categoryId}

)

    and content.content_type =#{request.contentType}


    and content.title like concat('%',#{request.title},'%')


    and content.doc_id =#{request.doctorId}



    and content.status =#{request.status}


    and content.is_open =#{request.isOpen}

order by content.create_time desc

select

from cms_ih_patient_content


        and content_id = #{content.contentId}


        and line_id = #{content.lineId}


        and module_id = #{content.moduleId}


        and category_id = #{content.categoryId}


        and del_flag = #{content.delFlag}

mapper.update:
update cms_ih_patient_content set del_flag = #{content.delFlag},update_user = #{content.updateUser}, update_time = #{content.updateTime}


        and content_id = #{content.contentId}


        and line_id = #{content.lineId}


        and module_id = #{content.moduleId}


        and category_id = #{content.categoryId}

mapper.select:
SELECT content.id id, patient.module_id moduleId, patient.category_id categoryId, content.title title, content.cover cover, content.create_time creatTime FROM cms_content content INNER JOIN cms_ih_patient_content patient ON content.id = patient.content_id
patient.line_id = 3 and patient.del_flag = 0
and patient.module_id = #{request.moduleId}
and patient.category_id = #{request.categoryId}
and content.content_type =#{request.contentType}
and content.status =#{request.status}
and content.is_open =#{request.isOpen}
order by patient.category_rank desc,content.create_time desc
