mapper.select:
select
    ee.id, ee.user_id, ee.eid, ee.code, ee.type, ee.parent_id, ee.position_id, ee.admin_flag, ee.status,
    ee.create_user, ee.create_time, ee.update_user, ee.update_time, ee.remark
from enterprise_employee ee
inner join user u on u.id = ee.user_id

left join enterprise_employee_department eed on eed.eid = ee.eid and eed.user_id = ee.user_id and eed.del_flag = 0



        and ee.eid = #{request.eid}


        and u.id = #{request.userId}


        and u.name like concat('%',#{request.name},'%')


        and u.mobile like concat('%',#{request.mobile},'%')


        and ee.code like concat('%',#{request.code},'%')


        and ee.type = #{request.type}


        and ee.status = #{request.status}


        and eed.department_id = #{request.departmentId}


        and ee.position_id = #{request.positionId}

    and ee.del_flag = 0
    and u.status != 9
